  public static void gui() {
    JFrame frame = new JFrame("Choose where to export the Project");
    frame.setSize(300, 200);
    frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
    JButton folderButton = new JButton("Select Folder");
    final JTextField folderPathField = new JTextField();
    folderPathField.setEditable(false);
    JButton actionButton = new JButton("Export HTML");
    frame.setLayout(new BoxLayout(frame.getContentPane(), BoxLayout.Y_AXIS));
    frame.add(folderButton);
    frame.add(folderPathField);
    frame.add(actionButton);
    folderButton.addActionListener((ev) -> selectFolder(folderPathField));
    actionButton.addActionListener((e) -> triggerAction(folderPathField.getText()));
    frame.setVisible(true);
  }
  private static void selectFolder(JTextField folderPathField) {
    JFileChooser chooser = new JFileChooser();
    chooser.setFileSelectionMode(JFileChooser.DIRECTORIES_ONLY);
    int returnValue = chooser.showOpenDialog(null);
    if (returnValue == JFileChooser.APPROVE_OPTION) {
      folderPathField.setText(chooser.getSelectedFile().getAbsolutePath());
      path = chooser.getSelectedFile().getAbsolutePath();
    }
  }
  private static void triggerAction(String folderPath) {
    if (folderPath.isEmpty()) {
      JOptionPane.showMessageDialog(null, "No folder selected!", "Warning", JOptionPane.WARNING_MESSAGE);
    } else {
      writeToHtml(map_ActivityBoard.generateActivitiesHtml(), path + "/activity" + ".html");
      writeToHtml(map_MotorTable.generateMotorTableHtml(), path + "/motor" + ".html");
      writeToHtml(generateWellHtml(), path + "/well" + ".html");
      writeToHtml(generateIndex(), path + "/index" + ".html");
      writeToHtml(map_WorkersTable.generateWorkersTableHtml(), path + "/workers" + ".html");
      JOptionPane.showMessageDialog(null, "Saved to: " + folderPath, "Folder Path", JOptionPane.INFORMATION_MESSAGE);
    }
  }
  public static void writeToHtml(StringBuilder flow, String name) {
    try (BufferedWriter writer = new BufferedWriter(new FileWriter(name))) {
      writer.append(flow.toString());
    } catch (IOException e) {
      throw new RuntimeException(e);
    }
  }
